            'files' => $data[$id]['files'][$lang] ?? [],
            'evaluations' => $data[$id]['evaluations'][$lang] ?? [],
            'content' => $data[$id]['content'][$lang] ?? [],
            'ogDescription' => $data[$id]['ogDescription'][$lang] ?? [],
            'arrangements' => $data[$id]['arrangements'][$lang] ?? [],
        );
    }

}
